import React from 'react';
import HeaderMenu from '../src/components/HeaderMenu.js';
import headerImg from '../src/images/headerImg.png'

class App extends React.Component {

  render() {

    return (
      <div style={{}}>
        <header style={{display: 'flex', height: 80}}>

          <span style={{flex: 1, alignItems: 'center', display: 'flex', justifyContent: 'center'}}>
            <p style={{fontFamily: 'Roboto', fontSize: 20, flexDirection: 'column', display: 'flex', lineHeight: 1}}>
              <span>
                <span style={{color: 'red'}}>N</span>
                <span style={{color: 'green'}}>O</span>
                <span style={{color: 'pink'}}>V</span>
                <span style={{color: 'gray'}}>A</span>
                <span style={{color: 'blue'}}>S</span>
              </span>
              <span style={{fontSize: 10, textDecoration: 'overline', display: 'flex', justifyContent: 'flex-end'}}>Logo</span>
            </p>
          </span>

          <span style={{flex: 2, alignItems: 'center', display: 'flex'}}>
            <HeaderMenu style={{}} />
          </span>

        </header>

        <div style={{backgroundImage: `url(${headerImg})`, width: '100%', height: 550, backgroundPosition: 'center', backgroundSize: 'cover', backgroundRepeat: 'no-repeat'}}>
          <span style={{fontFamily: 'Roboto'}}>Giải pháp quản lý toà nhà (BMS)</span> <hr/>
          <span style={{fontFamily: 'Roboto'}}>Tích hợp, điều khiển, giám sát và phối hợp các hệ thống cơ - điện trong toà nhà</span>
        </div>
      </div>
    );
  }
}

export default App;
